//示例生成emoji图片输入
function renderEmoji() {
    $("#emojiArea").empty();
    var html = '';
    for (var j = 0; j < emojiImg.length; j++) {
        if (j % 10 == 0 && j != 0) {
            html += '<br/>';
        }
        html +=
            '<img onclick="f(this)" name="' + emojiName[j] + '" style="display: inline;vertical-align: middle;" src="' + emojiImg[j]
            + '" /></li>';
    }
    $("#emojiArea").append(html);
}

//点击表情
function f(that) {
    console.log(that.getAttribute('name'));
    var oldText = $('#promoteText').val();
    var newText = oldText + that.getAttribute('name');

    if (newText.length > 50) {
        alert("限制输入500字")
    } else {
        $('#promoteText').val(newText);
    }

}

//点击表情按钮
function emojiClick(that) {
    var src = that.getAttribute('src');
    if (src == emojiImg[0]) {
        that.setAttribute('src', emojiImg[Math.round(Math.random() * 10) + 1]);
        renderEmoji();
        $("#emojiArea").attr("style", "display:block;");//显示div
    } else {
        that.setAttribute('src', emojiImg[0]);
        $("#emojiArea").attr("style", "display:none;");//隐藏div
    }
}
